class Gun:    def __init__(self, name):        self.moudle = name        self.bullet_count = 0    def add_bullet(self, count):        self.bullet_count += count    def shoot(self):        if self.bullet_count <= 0:            return        self.bullet_count -= 1        print('快没有子弹了')M4A1 = Gun('m4a1')M4A1.shoot()class Soldier:    def __init__(self, name):        # 定义新兵的姓名，再定义一把枪        self.name = name        self.gun = None    def fire(self):        # 判断士兵是否有枪        # 枪装填子弹        # 发射子弹        if self.gun is None:            print("%s not have gun" % self.name)            return        self.gun.add_bullet(50)        self.gun.shoot()san = Soldier("许三多")# san.gun = M4A1san.fire()# 可以将属性在初始化函数中初始化# 一个对象的属性可以是另外一个类的属性# 一个对象的属性可以是另外一个类的属性# 一个对象的属性可以是另外一个类的属性# 在对象的其他代码中直接进行使用# is 判断对象的引用地址是都相同# is 判断对象的引用地址是都相同 身份运算符， 身份运算符， 身份运算符# is 引用地址 == 值# None 是一个空对象