#!/usr/bin/env python# --coding:utf-8 --class A:    def __init__(self):        self.num1 = 100        self.__num2 = 200    def __test(self):        print('%d, %d' % (self.num1, self.__num2))    def test(self):        print('%d, %d' % (self.num1, self.__num2))class B(A):    def demo(self):        print('访问父类的私有属性 %d' % 1)        super().test()a = A()sub = B()sub.demo()# 属性在初始化方法中定义# 属性在初始化方法中定义