#! /usr/bin/env python#class A:    def __init__(self):        self.name = 'A'    def test(self):        print('I am a %s' % self.name)class B:    def __init__(self):        self.name = 'B'    def test(self):        print('I am a %s' % self.name)class C(A, B):    passc = C()c.test()# 多继承时， 父类有相同的方法，调用哪个方法呢，在使用多继承时# MRO method resolution order 方法搜索print(C.__mro__)print(C.mro())# 使用mro得到的顺序调用方法