class Tool:    count = 0    def __init__(self, name):        self.name = name        Tool.count += 1    @classmethod    def show_tools_count(cls):        print('tools count is %d' % cls.count)    @classmethod    def minus_tools(cls):        cls.count -= 1tool1 = Tool('chos')Tool.show_tools_count()tool2 = Tool('oshc')Tool.show_tools_count()tool3 = Tool('ocsh')Tool.minus_tools()Tool.show_tools_count()