class Game(object):    top_score = 0    def __init__(self, player_name):        self.player_name = player_name    @staticmethod    def show_help_message():        print('''source name: help message''')    @classmethod    def get_top_score(cls):        print('the max score is %.2f' % cls.top_score)    def start_game(self):        print('[%s] begin play' % self.player_name)Game.show_help_message()Game.get_top_score()ming = Game('Ryan')ming.start_game()