# !/usr/bin/env python# -- coding:utf-8 --# 小猫爱吃鱼，小猫要喝水class Cat:    '''cat link eat fish.    cat need drink water.    '''    def __init__(self):        self.name = 'Tom'        print("This is a init function.")    def eat(self):        print("%s like eat fish." % self.name)    def drink(self):        print("cat need drink water.")    def play(what):        print("%s play what" % what)tom = Cat()print(tom.name)tom.name = 'jack'print(tom.name)tom.eat()# self.attr = what# self.attr = what# self.attr = what# self.attr = what# self.attr = what