# !/usr/bin/env python# -- coding:utf-8 --# 小猫爱吃鱼，小猫要喝水class Cat:    '''cat link eat fish.    cat need drink water.    '''    def __init__(self, name, color, size):        self.name = name        self.color = color        self.size = size        print("This is a init function.")    def eat(self):        print("%s like eat fish." % self.name)    def drink(self):        print("cat need drink water.")    def __del__(self):        print("class will be done")tom = Cat(name='Tom', color='Yello', size='Big')print(tom.name)tom.name = 'jack'print(tom.name)tom.eat()print(dir(tom))print(format(tom))# tom是一个全局变量，所以方法全部执行完，再调用del# tom是一个全局变量，当方法全部执行完，在调用del# tom是一个全局变量，当方法全部执行完，再调用del# tom是一个全局变量，当代码全部执行完，再调用del# 当对象从内存中销毁之前，系统会自动帮我们调用del方法