# !/usr/bin/env python# -- coding:utf-8 --# 小猫爱吃鱼，小猫要喝水class Cat:    '''cat link eat fish.    cat need drink water.    '''    def __init__(self, name, color, size):        self.name = name        self.color = color        self.size = size        print("This is a init function.")    def eat(self):        print("%s like eat fish." % self.name)    def drink(self):        print("cat need drink water.")    def play(what):        print("%s play what" % what)    def __str__(self):        return 'i am a cat'    def __doc__(self):        return 'I am a cat doc'tom = Cat(name='Tom', color='Yello', size='Big')print(tom.name)tom.name = 'jack'print(tom.name)tom.eat()print(tom)# doc和str的区别# doc 定义函数的注释， str定义函数的自定义信息