# !/usr/bin/env python# -- coding:utf-8 --# 小猫爱吃鱼，小猫要喝水class Cat:    '''cat link eat fish.    cat need drink water.    '''    def __init__(self, name, color, size):        self.name = name        print("This is a init function.")    def eat(self):        print("%s like eat fish." % self.name)tom = Cat(name='Tom', color='Yello', size='Big')print(tom.name)tom.name = 'jack'print(tom.name)tom.eat()# 名词提炼法，将需求的名词提炼出来# 小明体重10kg，# 小明每次跑步减1kg# 每次吃东西加1kgclass Person:    def __init__(self, name, weight):        self.name = name        self.weight = weight    def __str__(self):        return "小明的体重是%d" % self.weight    def run(self):        self.weight -= 1    def eat(self):        self.weight += 1ming = Person('ming', 75)print(ming.__str__())ming.run()print(ming.__str__())ming.eat()print(ming.__str__())# 封装到一个抽象的类中# 封装到一个抽象的类中